Analytics.Script:
create temporary table com_wso2telco_userstatus_he_msisdn_not_found using CarbonAnalytics options (tableName "COM_WSO2TELCO_USERSTATUS", schema "sessionID STRING,status STRING,sourceIP STRING,forwardedIP STRING,_timestamp LONG -i", incrementalParams "user_status_he_msisdn_not_found, DAY");

create temporary table com_wso2telco_userstatus_meta_he_msisdn_not_found using CarbonAnalytics options (tableName "COM_WSO2TELCO_USERSTATUS_META", schema "sessionID STRING,sourceIP STRING,operator STRING,appId STRING,msisdn STRING,isMSISDNHeader BOOLEAN,userAgent STRING,consumerKey STRING,state STRING,nonce STRING,scope STRING,acrValue STRING,loginHint STRING,isNewUser BOOLEAN,telcoScope STRING,status STRING,_timestamp LONG -i", incrementalParams "user_status_meta_he_msisdn_not_found, DAY");

create temporary table com_wso2telco_operatoripranges_he_msisdn_not_found using CarbonAnalytics options (tableName "COM_WSO2TELCO_OPERATORIPRANGES", schema "id LONG,operator STRING, isEnable BOOLEAN, minValue STRING, maxValue STRING, _timestamp LONG -i" );

create temporary table com_wso2telco_summary_he_msisdn_not_found using CarbonAnalytics options (tableName "COM_WSO2TELCO_SUMMARY_HE_MSISDN_HEADER_INVALID",schema "iprangecheck STRING -i, operator STRING -i, sessionID STRING -i, msisdn STRING -i, sourceIP STRING -i, appId STRING -i, timestring STRING -i, _timestamp LONG -i", primaryKeys "iprangecheck,operator,sessionID,sourceIP,_timestamp,", mergeSchema "false");

INSERT INTO TABLE com_wso2telco_summary_he_msisdn_not_found
select IF(iprangecheck='1',"VALID","INVALID") as iprangecheck, operator, sessionID , msisdn, sourceIP, appId,  timestring, _timestamp
from (
    select max(iprangecheck) as iprangecheck, first(operator) as operator, sessionID , first(msisdn) as msisdn, first(sourceIP) as sourceIP, first(appId) as appId, getFormattedTimeString(first(_timestamp)) as timestring, first(_timestamp) as _timestamp
    from (
        select IF(checkIpRange(ipranges.minValue , ipranges.maxValue, operatorforward.sourceIP),1,0) as iprangecheck , operatorforward.sessionID, operatorforward.operator, operatorforward.msisdn, operatorforward.sourceIP, operatorforward.appId, _timestamp
        FROM
        (
            select minValue, maxValue, operator, isEnable
            FROM com_wso2telco_operatoripranges_he_msisdn_not_found
            where isEnable = true
        ) as ipranges
        INNER JOIN (
            select ustatus.sessionID, ustatus.sourceIP, meta.operator, meta.msisdn, meta.appId, ustatus._timestamp
            from(
                select sessionID
                from
                (
                    select distinct sessionID
                    from com_wso2telco_userstatus_he_msisdn_not_found
                    where status='HE_AUTH_PROCESSING_FAIL' and sourceIP != 'null'
                    EXCEPT
                        select distinct sessionID
                        from com_wso2telco_userstatus_he_msisdn_not_found
                        where status='IP_HEADER_NOT_FOUND' or status = 'IP_HEADER_NOT_IN_RANGE' or status = 'LOGINHINT_MISMATCH'
                ) as heFailed
                    EXCEPT
                        select distinct sessionID
                        from com_wso2telco_userstatus_meta_he_msisdn_not_found
                        where isMSISDNHeader = true
            ) as hefailed_msisdnnotfound INNER JOIN
                (
                    select sessionID, first(operator) as operator, min(msisdn) as msisdn, first(appId) as appId
                    from com_wso2telco_userstatus_meta_he_msisdn_not_found
                    where msisdn IS NOT NULL and operator IS NOT NULL and msisdn != "false" group by sessionID
                ) as meta on meta.sessionID = hefailed_msisdnnotfound.sessionID
                INNER JOIN
                (
                    select sessionID, max(sourceIP) as sourceIP, min(_timestamp) as _timestamp
                    from com_wso2telco_userstatus_he_msisdn_not_found
                    group by sessionID
                ) as ustatus on ustatus.sessionID = hefailed_msisdnnotfound.sessionID
            )as operatorforward on ipranges.operator = operatorforward.operator
        )as iprangecheck group by sessionID
    )as he_msisdn_not_found_report;

INCREMENTAL_TABLE_COMMIT user_status_he_msisdn_not_found;
INCREMENTAL_TABLE_COMMIT user_status_meta_he_msisdn_not_found;
